import React from "react";
import "./App.css";
import { SearchBar } from "../SearchBar/SearchBar";
import { SearchResults } from "../SearchResults/SearchResults";
import { Playlist } from "../Playlist/Playlist";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchResults: [
        //在本js文件中设置由Spotify API获取结果，传递给其他组件渲染
        { name: "name1", artist: "artist1", album: "album1", id: "id1" },
        { name: "name2", artist: "artist2", album: "album2", id: "id2" },
        { name: "name3", artist: "artist3", album: "album3", id: "id3" },
      ],
      playlistName: 'My Playlist',
      playlistTracks: [
        { name: "playlistName1", artist: "playlistArtist1", album: "playlistAlbum1", id: "id4" },
        { name: "playlistName2", artist: "playlistArtist2", album: "playlistAlbum2", id: "id5" },
        { name: "playlistName3", artist: "playlistArtist3", album: "playlistAlbum3", id: "id6" },
      ]
    }
  }

  render() {
    return (
      <div>
        <h1>
          Ja<span className="highlight">mmm</span>ing
        </h1>
        <div className="App">
          <SearchBar />
          <div className="App-playlist">
            <SearchResults searchResults={this.state.searchResults}/>
            {/* 将搜索结果传递给SearchResult.js */}
            <Playlist playlistName={this.state.playlistName} playlistTracks={this.state.playlistTracks}/>
            {/* 将搜索结果传递给SearchResult.js */}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
