import React from "react";
import "./App.css";
import { SearchBar } from "../SearchBar/SearchBar";
import { SearchResults } from "../SearchResults/SearchResults";
import { Playlist } from "../Playlist/Playlist";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchResults: [
        //在本js文件中设置由Spotify API获取结果，传递给其他组件渲染
        { name: "name1", artist: "artist1", album: "album1", id: "id1" },
        { name: "name2", artist: "artist2", album: "album2", id: "id2" },
        { name: "name3", artist: "artist3", album: "album3", id: "id3" },
      ],
      playlistName: "My Playlist",
      playlistTracks: [
        {
          name: "playlistName1",
          artist: "playlistArtist1",
          album: "playlistAlbum1",
          id: "id4",
        },
        {
          name: "playlistName2",
          artist: "playlistArtist2",
          album: "playlistAlbum2",
          id: "id5",
        },
        {
          name: "playlistName3",
          artist: "playlistArtist3",
          album: "playlistAlbum3",
          id: "id6",
        },
      ],
    };
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
  }

  addTrack(track) {
    let tracks = this.state.playlistTracks;
    if (tracks.find((savedTrack) => savedTrack.id === track.id)) {
      //find后括号内的形参代表数组中的元素，名字可以随便起，此处起为savedTrack
      return;
    } else {
      tracks.push(track);
      this.setState({ playlistTracks: tracks });
      //执行本方法后自动更新（渲染）PlaylistTracks的内容
    }
  }

  removeTrack(track) {
    let tracks = this.state.playlistTracks;
      tracks = tracks.filter(currentTrack => currentTrack.id !== track.id);
      //filter后括号内的形参代表数组中的元素，名字可以随便起，此处起为currentTrack
      //当playlistTracks中的某对象的id等于选中的对象（即track）的id时，该id的track被筛出新的tracks之列，也即删去了该track
      this.setState({ playlistTracks: tracks });
  }

  render() {
    return (
      <div>
        <h1>
          Ja<span className="highlight">mmm</span>ing
        </h1>
        <div className="App">
          <SearchBar />
          <div className="App-playlist">
            <SearchResults
              searchResults={this.state.searchResults}
              onAdd={this.addTrack}
            />
            {/* 将搜索结果传递给SearchResult.js...最终在Track组件中得到渲染 */}
            {/* 将addTrack方法作为state change method传递给SearchResult.js */}
            <Playlist
              playlistName={this.state.playlistName}
              playlistTracks={this.state.playlistTracks}
              onRemove={this.state.playlistTracks}
            />
            {/* 将搜索结果传递给Playlist.js...最终在Track组件中得到渲染 */}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
