const clientID = "a6da220db2624ca192ba9aa8a9ef5485";
const redirectURI = "http://localhost:3000/";

let accessToken;

const Spotify = {
  getAccessToken() {
    if (accessToken) {
      return accessToken;
    }

    //Check for an access token match
    const accessTokenMatch = window.location.href.match(/access_token=([^&]*)/);
    const expiresInMatch = window.location.href.match(/expires_in=([^&]*)/);

    if (accessTokenMatch && expiresInMatch) {
      accessToken = accessTokenMatch[1];
      const expiresIn = Number(expiresInMatch[1]);
      //This clears the parameters, allowing us to grab a new access token when it expires
      window.setTimeout(() => (accessToken = ""), expiresIn * 1000);
      window.history.pushState("Access Token", null, "/");
      return accessToken;
    } else {
      const accessURI = `https://accounts.spotify.com/authorize?client_id=${clientID}&response_type=token&scope=playlist-modify-public&redirect_uri=${redirectURI}`;
      window.location = accessURI;
    }
  },

  async search(term) {
    const accessToken = Spotify.getAccessToken();
    const endpoint = `https://api.spotify.com/v1/search?type=track&q=${term}`;
    const response = await fetch(endpoint, {
          headers: {
              Authorization: `Bearer ${accessToken}`,
          },
      });
      if (response.ok) {
          return response.json();
      }
      const jsonResponse = undefined;
      if (!jsonResponse.tracks) {
          return [];
      } else {
          return jsonResponse.tracks.items.map((track) => ({
              //为什么要有items??
              id: track.id,
              name: track.name,
              artist: track.artists[0].name,
              album: track.album.name,
              uri: track.uri,
          }));
      }
  },

  savePlaylist(playlistName, trackURIs) {
    if (!playlistName || !trackURIs) {
      return;
    }
    const accessToken = Spotify.getAccessToken();
    const headers = {
      Authorization: `Bearer ${accessToken}`,
    };
    let userID;
    let playlistID;
    const endpoint = "https://api.spotify.com/v1/me";
    return fetch(endpoint, { headers: headers })
      .then((response) => {
        return response.json();
      })
      .then((jsonResponse) => {
        userID = jsonResponse.tracks.items.id;
        return fetch("https://api.spotify.com/v1/users/user_id/playlists", {
          method: "POST",
          headers: {
          },
          body: JSON.stringify({name: "playlistName"})
          ,
        })
          .then((response) => {
            return response.json();
          })
          .then((jsonResponse) => {
            playlistID = jsonResponse.id;
          });
      });
  },
};

export default Spotify;
